{
    "Type": "Text",
    "Text": "Partio is a multi-tenant RESTful platform that accepts semantic cells with a chunking and embedding configuration, and returns chunked text with computed embeddings. It supports multiple chunking strategies including fixed token count, sentence-based, and paragraph-based approaches.",
    "ChunkingConfiguration": {
        "Strategy": "{{chunkStrategy}}",
        "FixedTokenCount": {{fixedTokenCount}},
        "OverlapCount": {{overlapCount}},
        "OverlapStrategy": "SlidingWindow",
        "ContextPrefix": "{{contextPrefix}}"
    },
    "EmbeddingConfiguration": {
        "Model": "{{embeddingModel}}",
        "L2Normalization": false
    },
    "Labels": ["test"],
    "Tags": {
        "source": "postman"
    }
}

{
    "Type": "Text",
    "Text": "First sentence here. Second sentence here. Third sentence goes here. And a fourth one too.",
    "ChunkingConfiguration": {
        "Strategy": "SentenceBased",
        "FixedTokenCount": {{fixedTokenCount}},
        "OverlapCount": {{overlapCount}},
        "OverlapStrategy": "SentenceBoundaryAware"
    },
    "EmbeddingConfiguration": {
        "Model": "{{embeddingModel}}",
        "L2Normalization": false
    }
}

{
    "Type": "Text",
    "Text": "First paragraph content here.\n\nSecond paragraph content here.\n\nThird paragraph content here.",
    "ChunkingConfiguration": {
        "Strategy": "ParagraphBased",
        "FixedTokenCount": {{fixedTokenCount}},
        "OverlapCount": {{overlapCount}}
    },
    "EmbeddingConfiguration": {
        "Model": "{{embeddingModel}}",
        "L2Normalization": false
    }
}

{
    "Type": "Code",
    "Text": "function fibonacci(n) {\n  if (n <= 1) return n;\n  return fibonacci(n - 1) + fibonacci(n - 2);\n}\n\nfor (let i = 0; i < 10; i++) {\n  console.log(fibonacci(i));\n}",
    "ChunkingConfiguration": {
        "Strategy": "{{chunkStrategy}}",
        "FixedTokenCount": {{fixedTokenCount}}
    },
    "EmbeddingConfiguration": {
        "Model": "{{embeddingModel}}",
        "L2Normalization": false
    }
}

{
